// Ce script transforme un bloc de texte brut (rapport UBPHAR) en un tableau JSON structur√©

const rawText = $json["text"];

// üîπ Nettoyer le texte et s√©parer en lignes utiles
const lines = rawText
  .split("\n")
  .map(l => l.trim())
  .filter(l => l.length > 0); // On supprime les lignes vides

// üîπ Extraction de la date dans l‚Äôent√™te (ex: "Etat au 30 avril 2025")
const dateMatch = rawText.match(/Etat au\s+([\d]{1,2}\s+\w+\s+\d{4})/i);
const DateStatement = dateMatch ? dateMatch[1] : null;

// Tableau final des produits
let produits = [];
let current = null;

/**
 * V√©rifie si une ligne correspond √† un code produit (ex: ALFA017)
 */
function isCodeProduit(line) {
  return /^[A-Z]{4}\d{3,}$/.test(line);
}

/**
 * V√©rifie si une ligne correspond √† des chiffres de stock/ventes
 */
function isLigneChiffres(line) {
  return /^\d+(\s+-?\d+)*$/.test(line);
}

/**
 * Ajoute le produit en cours au tableau si valide
 */
function pushProduitIfValid() {
  if (current && current.code) {
    produits.push({ ...current });
  }
}

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];

  if (isCodeProduit(line)) {
    // Sauvegarde le produit pr√©c√©dent
    pushProduitIfValid();

    // Nouveau produit
    current = {
      code: line,
      designation: "",
      stock: 0,
      ventes: []
    };

    // La d√©signation est compos√©e des lignes suivantes jusqu‚Äô√† rencontrer une ligne chiffres ou un autre code
    let j = i + 1;
    let designationParts = [];
    while (j < lines.length && !isCodeProduit(lines[j]) && !isLigneChiffres(lines[j])) {
      designationParts.push(lines[j]);
      j++;
    }
    current.designation = designationParts.join(" ");

    // On place l‚Äôindex juste avant la prochaine √©tape
    i = j - 1;
  }

  else if (isLigneChiffres(line) && current) {
    // On r√©cup√®re les valeurs num√©riques
    const chiffres = line.split(/\s+/).map(Number);
    current.stock = chiffres[0] || 0;
    current.ventes = chiffres.slice(1);
  }
}

// Ajouter le dernier produit
pushProduitIfValid();

// üîπ Mapper dans un format clair et standardis√©
return produits.map(p => ({
  Code: p.code,
  Designation: p.designation,
  Stock: p.stock || 0,              // ‚¨ÖÔ∏è renomm√©
  ventes_avril: p.ventes[0] || 0,
  ventes_mars: p.ventes[1] || 0,
  ventes_fevrier: p.ventes[2] || 0,
  ventes_janvier: p.ventes[3] || 0,
  ventes_decembre: p.ventes[4] || 0,
  ventes_novembre: p.ventes[5] || 0,
  DateStatement                  // ‚¨ÖÔ∏è ajout de la date globale
}));

// R√©cup√©rer la sortie actuelle
const mergedArray = $input.all().reduce((acc, item) => [...acc, ...item.json.output], []);

return mergedArray.map(item => ({ json: item }));

// R√©cup√®re tous les inputs
const inputs = $input.all();

// Initialise le tableau de r√©sultat
let mergedFiles = [];

// Pour chaque entr√©e
inputs.forEach(input => {
  try {
    // Parse le JSON contenu dans stdout
    const files = JSON.parse(input.json.stdout);
    // Ajoute les fichiers au tableau fusionn√©
    mergedFiles = mergedFiles.concat(files.pngFiles);
  } catch (error) {
    // Gestion d'erreur si le JSON est invalide
    console.error("Erreur de parsing:", error);
  }
});

  // Retourne le tableau fusionn√©
  return [{ 
    json: {
      all_png_files: mergedFiles,
      total_count: mergedFiles.length
    }
  }];

/**
 * Ce que fait le code :

D√©tection automatique : Il v√©rifie si l'objet contient au moins une propri√©t√© de mois

Traitement diff√©renci√© :

Pour les objets avec donn√©es mensuelles : il les transforme en entr√©es individuelles par mois

Pour les autres objets : il les retourne intacts

Pr√©servation des donn√©es : Aucune donn√©e n'est perdue
 */

const items = $input.all();
let result = [];

// Liste des mois possibles
const moisKeys = [
  "Janvier","Fevrier","Mars","Avril","Mai","Juin",
  "Juillet","Aout","Septembre","Octobre","Novembre","Decembre"
];

items.forEach(item => {
  const data = item.json;

  // V√©rifier si l'objet a au moins une propri√©t√© de mois
  const hasAnyMonth = moisKeys.some(mois => mois in data);

  if (hasAnyMonth) {
    // C'est un objet avec des donn√©es mensuelles
    moisKeys.forEach(mois => {
      if (mois in data) {
        const newItem = {...data};
        newItem.Month = mois;
        newItem.SalesStatement = data[mois] !== undefined ? data[mois] : null;
        // Supprimer les propri√©t√©s de mois individuelles pour √©viter la duplication
        moisKeys.forEach(m => {
          delete newItem[m];
        });
        result.push({ json: newItem });
      }
    });
  } else {
    // C'est un autre type d'objet, le retourner tel quel
    result.push({ json: {...data} });
  }
});

return result;
